import anime from 'animejs';
export function scrollDown() {
  var html = document.querySelector('html');
  var body = document.querySelector('body');
  var moveTo = window.innerHeight; // we need the html and the body to make the animation more smoother

  anime({
    targets: [html, body],
    scrollTop: moveTo,
    duration: 1500,
    easing: 'easeInOutQuad'
  });
}
