import React, {Fragment, useContext} from 'react'
import {StateContext} from '../../../utils/context/stateContext'
import Link from 'next/link'

function FooterMenu() {
  const {setState} = useContext(StateContext)
  const menus = ['home', 'about', 'projects', 'contacts']

  const click = () => {
    setState({
      menuIsOpen: !state.menuIsOpen,
      isTransitioning: setTransitionState(state.isTransitioning),
      exitMode: 'topExit',
      animation: 'topAnimation'
    })
  }



  let menusList = menus.map((menu, i)=>{
    return (
      <Fragment key={i}>
        <Link 
          key={menu === 'home' ? '/' : `/${menu}`}
          as={menu === 'home' ? '/' : `/${menu}`}
          href={`/${menu === 'home' ? '' : menu}`}>
          <a>
            <li 
              className='footer-menu'
              onClick={click}>{menu}</li>
          </a>
        </Link>
      {i < 3 ? <span>|</span> : null }
      </Fragment>
    )
  })

  return (
    <nav className='content-center footer-nav'>
      <ul className='content-center footer-nav-wrapper'>
        {menusList}
      </ul>
    </nav>
  )
}

export default FooterMenu;
