import React, { useState, createContext } from 'react'

// export InputContext 
export const InputData = createContext()

// the props is received and has a value that references
// the input data sets and returns data for the message form
export function InputDataContext (props) {

  const [state, setState] = useState({
    inputValue: ['', '', '', ''],
    alert: {
      message: '* required',
      color: 'go'
    },
    activeInput: null
  })

  // this function updates the state
  function setInputState (newState) {
    let a = /([^\s])/
    console.log(a.test('asdfasfd'))
    setState({
      ...state,
      ...newState
    })
  }
  
  // this function updates each values 
  // in the array of input values
  function setvalues(val, i){
    let values = state.inputValue
    values[i] = val
    setInputState({ inputValue: values })
  }
  


  function submitHandler () {
    let inputvalues = state.inputValue
    if(checkName(inputvalues[0])) {
      setInputState({
        alert: {
          message: 'Please enter a valid name.',
          color: 'alert'
        },
        activeInput: 'name'
      })
    }

    
  }

  return (
    <InputData.Provider value={{
        alert: state.alert, 
        inputValue: state.inputValue,
        activeInput: state.activeInput,
        setValues: setvalues,
        submitHandler: submitHandler}}>
      {props.children}
    </InputData.Provider>
  )
}


function checkName (value){
