import {useRef} from 'react'

export const refs = () => {
  const name = useRef(null)
  const email = useRef(null)
  const title = useRef(null)
  const message = useRef(null)
  return {name, email, title, message}
}

// these data are for the input value for the 
// message form in the contacts page
export const inputvalues = [
  {
    el: 'input',
    type: 'text',
    placeholder: 'Full name *',
    background: 'user.png',
    name: 'name',
    ref: refs().name
  },
  {
    el: 'input',
    type: 'email',
    placeholder: 'Email address *',
    background: 'envelop.png',
    name: 'email',
    ref: refs().email
  },
  {
    el: 'input',
    type: 'text',
    placeholder: `Subject: Just sayin' hi!`,
    background: 'title.png',
    name: 'hi',
    ref: refs().title
  },
  {
    el: 'textarea',
    placeholder: 'Message *',
    name: 'message',
    ref: refs().message
  },
]
