import React, { createContext } from 'react'
import {findData} from '../data/index'

// export PageData for contextapi access
export const PageData = createContext()

// the props is received and has a value that references
// the page data sets and returns data for that page
export function PageDataContext (props) {

  let data = sortProps(props)
  console.log(data)



  return (
    <PageData.Provider value={findData(props.pagesets)}>
      {props.children}
    </PageData.Provider>
  )
}

//  export Projectdata for the context api
export const ProjectData = createContext()

// the projectdata is received and has a value that references
// the project data and returns data for the project page
export function ProjectDataContext (props) {
  let projects = findData('projectlists').listofprojects.filter(proj => {
    return proj.title === props.projectsets
  })
  return (
    <ProjectData.Provider value={projects}>
      {props.children}
    </ProjectData.Provider>
  )
}



function sortProps (props) {
  if(props.pagesets) {
    return findData(props.pagesets)
  } else if(props.projectsets){
    return findData('projectlists').listofprojects.filter(proj => {
      return proj.title === props.projectsets
    })
  }
}
