import anime from 'animejs'

export const mouseHover = (el) =>{
  let tl = anime.timeline({})
  tl.add({
    targets: el,
    duration: 170,
    scale: 1.25,
    easing: 'easeInQuad'
  })
  .add({
    targets: el,
    duration: 30,
    scale: 1.2,
    easing: 'easeInQuad'
  })
}

export const mouseOut = (el) => {
  anime({
    targets: el,
    duration: 200,
    scale: 1.2,
    easing: easeInQuad
  })
}
