import React, { useState, createContext } from 'react'




// export InputContext 
export const InputData = createContext()

// the props is received and has a value that references
// the input data sets and returns data for the message form
export function InputDataContext (props) {

  const [state, setState] = useState({
    inputValue: ['', '', '', ''],
    alert: {
      message: '* required',
      color: 'go'
    }
  })
  
  // function that will set update the state
  // setApplicationState = (newState) => {
    // this.setState({
    //   ...this.state,
    //   ...newState
    // })
  // }
  
  function setvalues(val, i){
    console.log(state)
  }

  // setValues (val, i) {
  //   let stat = this.state
  //   console.log(val, i, stat)
  //   // let inputs = this.state.inputValue
  //   // inputs[i] = val
  //   // this.setApplicationState({
  //   //   inputValue: inputs
  //   // })
  // }


  return (
    <InputData.Provider value={{
        alert: state.alert, 
        inputValue: state.inputValue,
        setValues: setvalues }}>
      {props.children}
    </InputData.Provider>
  )
}
