import React from 'react'
// import {imageUrl} from '../../utils/common/common'
// import ScrollBtn from './ScrollBtn/ScrollBtn'
// import SocialIcons from './SocialIcon/SocialIcon'
import HeaderText from '../HeaderTextBlock/HeaderTextBlock'
import Subparagraphs from '../SubParagraphs/Subparagraphs'
import Credentials from '../Credential/Credential'
import Hero from './Hero/Hero'

// import {connect} from 'react-redux'
// import './Header.css'

function Header(props) {
  let data = props.data
  // console.log(data)
  
  let headerContent = (
    <div className='top-background-container'
      style={{background: 
        `url('/images/${data.background}.png') repeat center top`}} >



      <article className='header-text-block'>
        <div className="header-title-block">

          {data.withImage.isTrue ? 
            <Hero data={data.withImage}/> : null }

            <HeaderText data={data} />


          {data.subParagraph.isTrue ? 
            <Subparagraphs data={data} /> : null }

          {data.withCredentials.isTrue ? 
            <Credentials data={data} /> : null }

        </div>
      </article>

      
      {/* render social icons
      {data.headers.withSocials ? <SocialIcons /> : null }

      render scroll button
      {data.headers.withScrollButton.isTrue ? 
        <ScrollBtn data={data.headers.withScrollButton}/> : null} */}

    </div>
  )
  return (
    <header className='top' 
      style={{'background': data.colormodes.close.back}}>
      <div className="top-container">
        {headerContent}
      </div>
      <style jsx>{`
        .top-background-container {
          width: calc(100% - 18px);
          min-height: calc(100vh - 9px);
          margin: 0px 9px 0px 9px;
        }


        .header-text-block {
          width: 1000px;
          min-height: calc(100vh - 18px);
          margin: 0 auto;
          overflow: auto
        }

        .header-title-block {
          width: 100%;
          min-height: 200px;
          margin-top: calc(100vh - 43vh);
        }

        .scroll-btn-container{
          width: 20px;
          height: 120px;
          position: absolute;
          top: calc(100vh - 22vh);
          overflow: hidden;
          transition: all 0.15s ease-in;
        }

        .scroll-btn-container:hover {
          cursor: pointer;
          transform: translateY(10px);
          transition: all 0.45s ease-in;
        }

        .scroll-cover {
          width: 100%;
          height: 100%;
        }

        @media (max-width: 1568px) {
          .header-text-block {
            width: 950px;
          }
        }

        @media (max-width: 1468px) {
          .header-text-block {
            width: 875px;
          }
        }

        @media (max-width: 1368px) {
          .header-text-block {
            width: 820px;
          }
        }

        @media (max-width: 1268px) {
          .header-text-block {
            width: 750px;
          }
        }

        @media (max-width: 1168px) {
          .header-text-block {
            width: 700px;
          }
        }

        @media (max-width: 1068px) {
          .header-text-block {
            width: 720px;
          }
        }

        @media (max-width: 968px) {
          .header-text-block {
            width: 640px;
          }
        }

        /* set container screen to 720px when screen is on portrait */
        @media screen and (max-width: 896px) and (orientation: landscape) {
          .header-text-block {
            min-height: calc(720px - 18px);
          }
          .header-title-block {
            margin-top: calc(720px - 350px);
          }
          .scroll-btn-container {
            top: calc(720px - 220px);
          }
        }

        @media (max-width: 868px) {
          .header-text-block {
            width: 560px;
          }
        }

        @media (max-width: 768px) {
          .scroll-cover svg {
            display: none;
          }
        }

        @media (max-width: 668px) {
          .header-text-block {
            width: 530px;
          }
        }

        @media (max-width: 568px) {
          .header-text-block {
            width: 430px;
          }
        }

        @media (max-width: 468px) {
          .header-text-block {
            width: 96%;
          }
          .header-title-block {
            margin-top: calc(100vh - 270px);
          }
        }

        @media (max-width: 368px) {
          .header-text-block {
            width: 98%;
          }
          .header-title-block {
            margin-top: calc(100vh - 240px);
          }
        }
      `}</style>
    </header>
  )
}

// const mapStateToProps = (state) => {
//   return {
//     colorModes: state.state.colorModes,
//     headerPageData: state.state.pageData
//   }
// }

// export default connect(mapStateToProps)(Header)
export default Header
