import React, {useEffect, useContext} from 'react'
import {StateContext} from '../../utils/context/stateContext'
import BrandLogo from '../Svgs/BrandLogo/BrandLogo'
import {introAnim, openTopLayer, closeTopLayer} from '../../utils/pageanimations/toplayover/toplayover'
import {checkforsession} from '../../utils/sessiondata/sessionStorage'
import {motion} from 'framer-motion'
import anime from 'animejs'


const TopLayover = () =>{
  const {state, setState} = useContext(StateContext)
  console.log(state)

  if(state.isTransitioning ===  true) {
    // setTimeout(()=>{
    //   openTopLayer()
    // }, 1100)
    // setTimeout(() => {
    //   setState({ isTransitioning: false})
    // }, 1700)
  }
  // if(state.isTransitioning ===  false) {
  //   closeTopLayer
  //   setState({ isTransitioning: 'initial'})
  // }
  
  // useEffect(()=>{
  //   (async () => {
  //     const session = await checkforsession()
  //     session === 'initial' ? introAnim(false) : null
  //     session ? setState({isTransitioning : 'ready'}) : null
  //   })()
  // }, [])
  useEffect(()=>{
    // const el = document.querySelector('.top-layover-logo')
    // console.log(el)
    // if(state.exitMode === 'topExit') {
      
    //   const tl = anime.timeline({})
    //   tl.
    //   add({
    //     duration: 100,
    //     targets: el,
    //     opacity: 1,
    //     easing: 'linear'
    //   })
    //   .add({
    //     duration: 200,
    //   })
    //   .add({
    //     opacity: 0,
    //     duration: 50,
    //     targets: el,
    //     easing: 'linear'
    //   })
    // }
  })

  return (
    <motion.div 
      variants={topAnim}
      initial={state.isTransitioning}
      animate={state.animation}
      exit={state.exitMode}
      className="content-center top-layover">
        <motion.div 
          variants={logoAnim}
          initial={state.isTransitioning}
          animate={state.animation}
          exit={state.exitMode}
          className="top-layover-logo content-center">
          <BrandLogo colorMode={'#E3E1C3'}/>
        </motion.div>


      {/* <div className="top-layover-container content-center">
        <div className="top-layover-logo content-center"> */}
        {/* <BrandLogo 
          colorMode={'#E3E1C3'}/> */}
          {/* {props.hasSession ? null : 
          <p className='top-loading'
            style={{'color': '#F0EED2'}}>
              We are loading . . .</p> } */}
          {/* <p className='top-loading'
            style={{'color': '#F0EED2'}}>
              We are loading . . .</p> */}
        {/* </div>
      </div> */}
      {/* <div className="top-layover-runner">
        <motion.span 
          // variants={topAnim}
          // initial={state.isTransitioning ? 'initialOne' : 'initialTwo'}
          // animate='animate'
          // exit='exit'
          className='top-runner' 
          // style={{'background':'#CCCAAE'}}
          ></motion.span>
      </div> */}
    </motion.div> 
  )
}

export default TopLayover

let easing = [0.6, 0.05, 0.01, 0.99];

const logoAnim = {
  initial: {
    opacity: 0
  },
  initAnimate: {
    opacity: 0
  },
  initialOne:{
    opacity: 0.7 
  },
  initialTwo: {
    opacity: 0.7
  },
  topAnimation: {
    opacity: 0,
    transition: {
      duration: .15,
    }
  },
  bottomAnimation: {
    opacity: 0,
    transition: {
      duration: .25,
    }
  },
  topExit: {
    opacity: 0.7, 
    transition: {
      delay: .7,
      duration: .1,
      ease: easing
    }
  },
  bottomExit: {
    opacity: 0.7, 
    transition: {
      delay: .1,
      duration: .05,
      ease: easing
    }
  }
}


const topAnim = {
  initial: {
    width: '0%',
    right: 0
  },
  initAnimate: {
    width: '0%',
  },
  initialOne: {
    width: '100%',
    left: 0
  },
  initialTwo: {
    width: '100%',
    right: 0
  },
  topAnimation: {
    width: '0%',
    float: 'right',
    right: 0,
    transition: {
      // delay: 1.5,
      duration: .6,
      ease: easing
    }
  },
  bottomAnimation: {
    width: '0%',
    float: 'right',
    right: 0,
    transition: {
      // delay: 1.5,
      duration: .7,
      ease: easing
    }
  },
  topExit: {
    width: '100%',
    left: 0,
    transition: {
      delay: 15,
      duration: .4,
      ease: easing
    }
  },
  bottomExit: {
    width: '100%',
    left: 0,
    transition: {
      // delay: ,
      duration: .25,
      ease: easing
    }
  }
}
