import axios from 'axios'
import {getIP, getUserData, generateUrl, getTime} from './commonfunctions'

export async function saveMessageToFirebase (values) {

  // let message = {
  //   title: values[2] === '' ? `Just sayin' hi!` : values[2],
  //   name: values[0],
  //   email: values[1],
  //   message: values[3],
  //   time: getTime('timeOfVisit')
  // }
  let message = {
    title: 'ey',
    name: 'manski',
    email: 'mail.com',
    message: 'tsap bro',
    time: getTime('timeOfVisit')
  }
  let data = {
    message: message,
    userIP: await getIP(),
    userData: await getUserData()
  }
  
  let url = generateUrl('message')



  await axios.post(url, data)
    .then(res =>{
      if(res) {
        if(res.status === 200) {
          console.log(res)
        }
      }
    })
    .catch(err => {
      if (err) console.log(err)
    })
  
}
