export function scrollAnimation(page) {
  startScroll(page)
  window.addEventListener('scroll', ()=>{
    startScroll(page)
  })
  // return () => {
  //   window.removeEventListener('scroll', startScroll)
  // }
}

function startScroll(page) {
  console.log(page)
}
