import React, { createContext, useState } from 'react'

// export InputContext 
export const InputData = createContext()

// the props is received and has a value that references
// the page data sets and returns data for that page
export function InputDataContext () {
  const [inputValue, setInputValue] = useState(['', '', '', ''])
  const [alertMessage, setAlertMessage] = useState({
    message: '* required',
    color: 'go'
  })

  return (
    <InputDataContext.Provider value={inputValue, alertMessage}>
      {props.children}
    </InputDataContext.Provider>
  )
}
