import axios from 'axios'
import {getIP, getUserData, generateUrl, getTime} from './commonfunctions'

export async function saveMessageToFirebase (values) {

  let message = {
    title: values[2] === '' ? `Just sayin' hi!` : values[2],
    name: values[0],
    email: values[1],
    message: values[3],
    time: getTime('timeOfVisit')
  }

  let url = generateUrl('message')
  let userIP = await getIP()
  let userData = await getUserData()

  // axios.post(url, {message, getIP, getUserData})
  console.log(message, userIP, userData)
  


  
}
