import React, { useState, createContext } from 'react'

// export InputContext 
export const InputData = createContext()

// check if a string is empty
const ifEmptyString = /^\s*\S+.*/
// check if a string contains a number
const stringContainsNumber = /\d/
// check if email is valid
const isEmailValid = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/

// the props is received and has a value that references
// the input data sets and returns data for the message form
export function InputDataContext (props) {

  const [state, setState] = useState({
    inputValue: ['', '', '', ''],
    alert: {
      message: '* required',
      color: 'go'
    },
    activeInput: null
  })

  // this function updates the state
  function setInputState (newState) {
    setState({
      ...state,
      ...newState
    })
  }
  
  // this function updates each values 
  // in the array of input values
  function setvalues(val, i){
    console.log(i)
    let values = state.inputValue
    values[i] = val
    setInputState({ inputValue: values })
  }

  function submitHandler () {
    let values = state.inputValue
    let name = checkValue('name', values[0])
    let email = checkValue('email', values[1])
    let message = checkValue('message', values[3])

    if(name === 'nameIsEmpty'){
      setInputState(stateUpdateValue(name))
      return 
    } else if(name === 'hasNumber'){
      setInputState(stateUpdateValue(name))
      return 
    } else if (email === 'emailIsEmpty') {
      setInputState(stateUpdateValue(email))
    } else if (email === 'emailIsInvalid') {
      setInputState(stateUpdateValue(email))
    } else if (message === 'messageIsEmpty') {
      setInputState(stateUpdateValue(message))
    } else {
      setInputState(stateUpdateValue('process'))
    }
  }
  
  
  

  return (
    <InputData.Provider value={{
        alert: state.alert, 
        inputValue: state.inputValue,
        activeInput: state.activeInput,
        setValues: setvalues,
        submitHandler: submitHandler}}>
      {props.children}
    </InputData.Provider>
  )
}

// function to check if the name input is invalid
function checkValue (input, value){
  let valueIsNotEmpty = ifEmptyString.test(value)
  let valueHasNumber = stringContainsNumber.test(value)
  let emailIsValid = isEmailValid.test(value)

  if(input === 'name') {
    if(!valueIsNotEmpty) return 'nameIsEmpty' 
    if(valueHasNumber) return 'hasNumber'
  } else if(input === 'email'){
    if(!valueIsNotEmpty) return 'emailIsEmpty' 
    if(!emailIsValid) return 'emailIsInvalid'
  } else if (input === 'message') {
    if (!messageIsNotEmpty) return 'messageIsEmpty'
  }
}


function stateUpdateValue (val) {
  if(val === 'nameIsEmpty') {
    return state('Please enter your full name.', 'alert', 'name')
  } else if(val === 'hasNumber') {
    return state('Please enter a valid name', 'alert', 'name')
  } else if (val === 'emailIsEmpty'){
    return state('Please enter your email.', 'alert', 'email')
  } else if (val === 'emailIsInvalid') {
    return state('Please provide your email.', 'alert', 'email')
  } else if (val === 'messageIsEmpty') {
    return state('Please enter your message.', 'alert', 'message')
  } else if (val === 'process') {
    return state('Processing . . .', 'warning', null)
  }
}


function state(text, alert, input){
  return {
    alert: {
      message: text,
      color: alert
    },
    activeInput: input
  }
}
