import React, {useContext} from 'react'
import {StateContext} from '../../utils/context/stateContext'
import {inputvalues} from '../../utils/data/inputdata'

function Inputs() {
  const {state, setState} = useContext(StateContext)
  const values = state.inputvalues
  
  const inputchange = (val,i) => {
    let v = values
    v[i] = val
    
  }
  
  let inputs = inputvalues.map((input, i) => {
    return input.el === 'input' ? (
      <div className="input-container" key={i}>
        <i className="icon"
        style={{background: 
          `url('/images/${input.background}') no-repeat center center`}}></i>
        <input 
          className="input-field" 
          type={input.type} 
          placeholder={input.placeholder}
          name={input.name}
          value={values[i]}
          onChange={(e)=>{inputchange(e.target.value, i)}}/>
      </div>
    ) : (
      <div className="input-container" key={i}>
        <textarea 
          className="input-field message-area" 
          spellCheck={false}
          placeholder={input.placeholder} 
          name={input.name}
          value={values[i]}
          onChange={(e)=>{inputchange(e.target.value, i)}}>
        </textarea>
      </div>
    )
  })

  return (
    <div>
      {inputs}
    </div>
  )
}

export default Inputs
