import React from 'react'
// import {connect} from 'react-redux'
// import * as action from '../../../store/actions/index'
// import {mouseOut, mouseEnter} from '../../../utils/common/menuHoverEvents'
// import {openMenu, closeMenu} from '../../../utils/common/menuClickEvents'
// import {navHoverElements} from '../../../utils/common/elementSorter'
// import anime from 'animejs'
import Close from '../../../components/Svgs/Close/Close'
// import './NavbarMenu.css'

function NavbarMenu(props) {
  

  // click event 
  const clicked = async () => {
    if(!props.isAnimating) {
      setAnimate(true)
      if(!props.isOpen) {
        openMenuBtn()
      } else {
        closeMenuBtn()
      }
    }
  }

  // close menu function
  const closeMenuBtn = () => {
    const el = navHoverElements()
    // call function to close animation
    closeMenu(true)

    // set is animating after 1700 milsec to block multiple click events
    setAnimate(false)

    const n = document.querySelector('.navbar-container')
    
    let tl = anime.timeline({
      easing: 'easeInQuad',
      duration: 200,
    })
    tl.add({
        targets: n,
        opacity: 0,
      })
      .add({
        duration: 10,
        complete: function (anim) {
          props.setMenu(!props.isOpen)
        }
      })
      .add({
        targets: n,
        opacity: 1,
        complete: function (anim) {
          el.middle.style.color = props.colorModes.close.main          
        }
      })
    }

  // open menu function
  const openMenuBtn = () => {
    // call function to open menu animtion
    openMenu()

    // set is animating after 1700 milsec to block multiple click events
    setAnimate(false)
    const n = document.querySelector('.navbar-container')
    
    let tl = anime.timeline({
      easing: 'easeInQuad',
      duration: 200,
    })
    tl.add({
      targets: n,
      opacity: 0,
    })
    .add ({
      duration: 10,
      complete: function (anim) {
        props.setMenu(!props.isOpen)
      }
    })
    .add({
      targets: n,
      opacity: 1,
    })
  }

  // function to set isanimating state
  const setAnimate = (isAnim) => {
    isAnim ? props.setAnimating(isAnim) : 
    setTimeout(()=>{
      props.setAnimating(false)
    }, 1700)
  }

  // hover in functions and to set specific assigned colors
  const hoverIn = () => {
    props.isOpen ? mouseEnter(props.colorModes.open, 'open') :
     mouseEnter(props.colorModes.close, 'close')
  }

  // hover out functions and to set specific assigned colors
  const hoverOut = () => {
    props.isOpen ? mouseOut(props.colorModes.open, 'open') :
      mouseOut(props.colorModes.close, 'close')
  }

  // console.log(props.isAnimating)

  return (
    <div className="navbar-menu">
      <div className="front content-center">
        <span className="content-center front-text"
          style={{'color': props.colors.close.main}}
          onMouseOver={hoverIn}
          onMouseLeave={hoverOut}
          onClick={clicked} >
            {props.isOpen ? <Close /> : 'Menu'}</span>  
      </div>
      <div className="middle content-center">
        <span className="middle-span"></span>
      </div>
      <div className="back content-center">
        <span className="back-span"></span>
      </div>
      <style>{`
        .navbar-menu {
          height: 90px;
          width: 90px;
          float: right;
          margin-right: 180px;
          position: relative;
          top: -7.5px;
        }

        .navbar-menu div {
          width: 100%;
          height: 100%;
          position: absolute;
        }

        .front {
          z-index: 780;
        }

        .middle {
          z-index: 760;
        }

        .back {
          z-index: 740;
        }

        .middle-span, .back-span {
          width: 0;
          height: 0;
          border-radius: 50%;
        }

        .front-text {
          color: #EBE9CD;
          font-size: 1.18em;
          letter-spacing: -0.01em;
        }

        .front-text:hover {
          cursor: pointer;
        }

        .front svg {
          width: 25px;
        }

        @media (max-width: 1468px) {
          .navbar-menu {
            margin-right: 167px;
          }
        }

        @media (max-width: 1368px) {
          .navbar-menu {
            margin-right: 152px;
          }
        }

        @media (max-width: 1268px) {
          .navbar-menu {
            width: 85px;
            height: 85px;
            margin-right: 139px;
          }
          .front svg {
            width: 24px
          }
          .front-text{
            font-size: 1.17em;
          }
        }

        @media (max-width: 1168px) {
          .navbar-menu {
            margin-right: 126px;
          }
          .front svg {
            width: 23px
          }
          .front-text {
            font-size: 1.15em;
          }
        }

        @media (max-width: 1068px) {
          .navbar-menu {
            margin-right: 112px;
            width: 80px;
            height: 80px;
          }
          .front-text {
            font-size: 1.14em;
          }
        }

        @media (max-width: 968px) {
          .navbar-menu {
            margin-right: 96px;
          }
          .front svg {
            width: 22px
          }
        }

        @media (max-width: 868px) {
          .navbar-menu {
            width: 70px;
            height: 70px;
            margin-right: 80px;
          }
          .front svg {
            width: 21px
          }
          .front-text {
            font-size: 1.13em;
          }
        }

        @media (max-width: 768px) {
          .navbar-menu {
            width: 65px;
            height: 65px;
            margin-right: 64px;
          }
          .front svg {
            width: 20px
          }
          .front-text {
            font-size: 1.11em;
          }
        }

        @media (max-width: 668px) {
          .navbar-menu {
            top: -10px;
            margin-right: 48px;
            width: 60px;
            height: 60px;
          }
          .front svg {
            width: 19px
          }
          .front-text {
            font-size: 1.12em;
          }
        }

        @media (max-width: 568px) {
          .navbar-menu {
            width: 55px;
            height: 55px;
            margin-right: 25px;
            top: -7.5px;
          }
          .front svg {
            width: 17px
          }
          .front-text {
            font-size: 1em;
          }
        }

        @media (max-width: 468px) {
          .navbar-menu {
            top: -3px;
            width: 48px;
            height: 48px;
            margin-right: 25px;
          }
          .front svg {
            width: 13px
          }
          .front-text {
            font-size: 0.82em;
          }
        }
      `}</style>
    </div>
  )
}


// const mapStateToProps = (state) => {
//   return {
//     colorModes: state.state.colorModes,
//     isOpen: state.state.isOpen,
//     isAnimating: state.state.isAnimating,
//     activeRoute: state.state.activeRoute
//   }
// }

// const mapDispatchToProps = (dispatch) => {
//   return {
//     setAnimating: (val) => { dispatch(action.setAnimating(val)) },
//     setMenu: (val) => { dispatch(action.menuIsOpen(val)) }
//   }
// }

// export default connect(mapStateToProps, mapDispatchToProps)(NavbarMenu)
export default NavbarMenu
