export const variants = {
  initial: {
    opacity: 1,
  },
  enter: {
    // opacity: 1,
    transition: {
      duration: 0.25,
    },
  },
  topExit: {
    opacity: 0,
    transition: {
      delay: 1.6,
      duration: 0
    },
  },
  bottomExit: {
    opacity: 0,
    transition: {
      delay: .6,
      duration: 0.1
    },
  }
}
