import React, { Component, createContext } from 'react'

// export InputContext 
export const InputData = createContext()

// the props is received and has a value that references
// the input data sets and returns data for the message form
export default class InputDataContext extends Component {
  state = {
    inputValue: ['', '', '', ''],
    alert: {
      message: '* required',
      color: 'go'
    }
  }
  
  // function that will set update the state
  setApplicationState = (newState) => {
    this.setState({
      ...this.state,
      ...newState
    })
  }
  
  setValues (val, i) {
    console.log(val, i, this.state)
    // let inputs = this.state.inputValue
    // inputs[i] = val
    // this.setApplicationState({
    //   inputValue: inputs
    // })
  }



  render(){

    return (
      <InputData.Provider value={{
          alert: this.state.alert, 
          inputValue: this.state.inputValue,
          setValues: this.setValues
          }}>
        {this.props.children}
      </InputData.Provider>
    )
  }
}
