import React, {useEffect, useContext} from 'react'
import {StateContext} from '../../utils/context/stateContext'
import BrandLogo from '../Svgs/BrandLogo/BrandLogo'
import {introAnim, openTopLayer, closeTopLayer} from '../../utils/pageanimations/toplayover/toplayover'
import {checkforsession} from '../../utils/sessiondata/sessionStorage'
import {motion} from 'framer-motion'


const TopLayover = (props) =>{
  const {state, setState} = useContext(StateContext)
  console.log(state.isTransitioning, state.exitMode)

  if(state.isTransitioning ===  true) {
    // setTimeout(()=>{
    //   openTopLayer()
    // }, 1100)
    // setTimeout(() => {
    //   setState({ isTransitioning: false})
    // }, 1700)
  }
  // if(state.isTransitioning ===  false) {
  //   closeTopLayer
  //   setState({ isTransitioning: 'initial'})
  // }
  
  useEffect(()=>{
    (async () => {
      const session = await checkforsession()
      session === 'initial' ? introAnim(true) : null
      session ? setState({isTransitioning : 'ready'}) : null
    })()
  }, [])

  return (
    <motion.div 
      variants={topAnim}
      initial={state.isTransitioning}
      animate={state.isTransitioning === 'initial' ? 'initAnimate' : 'animate'}
      exit={state.exitMode ? 'bottomExit' : 'topExit'}
      className="top-layover">
      <div className="top-layover-container content-center">
        <div className="top-layover-logo content-center">
          <BrandLogo 
            colorMode={'#E3E1C3'}/>
          {/* {props.hasSession ? null : 
          <p className='top-loading'
            style={{'color': '#F0EED2'}}>
              We are loading . . .</p> } */}
          <p className='top-loading'
            style={{'color': '#F0EED2'}}>
              We are loading . . .</p>
        </div>
      </div>
      {/* <div className="top-layover-runner">
        <motion.span 
          // variants={topAnim}
          // initial={state.isTransitioning ? 'initialOne' : 'initialTwo'}
          // animate='animate'
          // exit='exit'
          className='top-runner' 
          // style={{'background':'#CCCAAE'}}
          ></motion.span>
      </div> */}
    </motion.div> 
  )
}

export default TopLayover

let easing = [0.6, 0.05, 0.01, 0.99];

const topAnim = {
  initial: {
    width: '100%',
    height: '100vh',
  },
  initAnimate: {
    height: '0%',
    transition: {
      delay: 4,
      duration: .3,
      ease: easing
    }
  },

  ready: {
    width: '0%',
    left: 0
  },

  initialOne: {
    width: '100%',
    left: 0
  },
  initialTwo: {
    width: '100%',
    right: 0
  },


  animate: {
    width: '0%',
    float: 'right',
    right: 0,
    transition: {
      // delay: 1.5,
      duration: .3,
      ease: easing
    }
  },

  topExit: {
    width: '100%',
    left: 0,
    transition: {
      delay: 0.8,
      duration: .3,
      ease: easing
    }
  },
  bottomExit: {
    width: '100%',
    left: 0,
    transition: {
      delay: .5,
      duration: .3,
      ease: easing
    }
  }
}
