import React, {Component, createContext} from 'react'

// global state context route
export const StateContext = createContext()

// state provder
export class StateProvider extends Component {
  
  // initial
  state= {
    activeRoute: 'home',
    menuIsOpen: false,
    menuIsAnimating: false,
    isTransitioning: 'initial',
    exitMode: false,
    animation: 'initial'
  }

  // function that will set update the state
  setApplicationState = (newState) => {
    this.setState({
      ...this.state,
      ...newState
    })
  }

  routeState = (from) => {
    // set the mode to its coresponding value if the privacy
    // button clicked is from the bottom
    let exitMode = fromTop ? 'topExit' : 'bottomExit'
    let animation = props.fromTop ? 'topAnimation' : 'bottomAnimation'

    if(from === 'top'){
      this.setState({
        menuIsOpen: !state.menuIsOpen,
        exitMode,
        isTransitioning: setTransitionState(state.isTransitioning),
        animation
      })
    }
    if(from === 'bottom') {
      setState({
        exitMode: 'bottomExit',
        isTransitioning: setTransitionState(state.isTransitioning),
        animation: 'bottomAnimation'
      })
    }
  }

  // function to get the current route
  setActiveRoute = (route) => {
    // if the current route does not match to the old route
    // then update the active route state
    if(route != this.state.activeRoute){
      this.setApplicationState({ activeRoute: route })
    }
  }

  render(){

    return(
      <StateContext.Provider value={{
        state: this.state,
        setState: this.setApplicationState,
        setActiveRoute: this.setActiveRoute
      }}>
        {this.props.children}
      </StateContext.Provider>
    )
  }
}

const mode = (from) => {
  return from === 'top' ? 'topExit' : 'bottomExit'
}

const animmode = (from) => {
  return from === 'bottom' ? 'topAnimation' : 'bottomAnimation'
}
