import React from 'react'
// import {imageUrl} from '../../utils/common/common'
// import ScrollBtn from './ScrollBtn/ScrollBtn'
// import SocialIcons from './SocialIcon/SocialIcon'
// import HeaderText from '../HeaderTextBlock/HeaderTextBlock'
// import Subparagraphs from '../SubParagraphs/Subparagraphs'
// import Credentials from '../../containers/Credentials/Credential/Credential'
// import Hero from './Hero/Hero'

// import {connect} from 'react-redux'
// import './Header.css'

function Header(props) {
  let data = props.data
  
  let headerContent = (
    <div className='top-background-container'
      style={{background: 
        `url('${data.headers.background}, 'png')}') repeat center top`}}
        >



      <article className='header-text-block'>
        <div className="header-title-block">

          {data.headers.withImage.isTrue ? 
            <Hero data={data.headers.withImage}/> : null }

            {/* <HeaderText data={data.headers} /> */}


          {/* {data.headers.subParagraph.isTrue ? 
            <Subparagraphs data={data.headers} /> : null }

          {data.headers.withCredentials.isTrue ? 
            <Credentials data={data.headers} /> : null } */}

        </div>
      </article>

      
      {/* render social icons
      {data.headers.withSocials ? <SocialIcons /> : null }

      render scroll button
      {data.headers.withScrollButton.isTrue ? 
        <ScrollBtn data={data.headers.withScrollButton}/> : null} */}

    </div>
  )
  return (
    <header className='top' 
      style={{'background': props.colorModes.close.back}}>
      <div className="top-container">
        {headerContent}
      </div>
      <style jsx>{`
        .top - background - container {
            width: calc(100 % -18 px);
            min - height: calc(100 vh - 9 px);
            margin: 0 px 9 px 0 px 9 px;
          }


          .header - text - block {
            width: 1000 px;
            min - height: calc(100 vh - 18 px);
            margin: 0 auto;
            overflow: auto
          }

          .header - title - block {
            width: 100 % ;
            min - height: 200 px;
            margin - top: calc(100 vh - 43 vh);
          }

          .scroll - btn - container {
            width: 20 px;
            height: 120 px;
            position: absolute;
            top: calc(100 vh - 22 vh);
            overflow: hidden;
            transition: all 0.15 s ease - in ;
          }

          .scroll - btn - container: hover {
            cursor: pointer;
            transform: translateY(10 px);
            transition: all 0.45 s ease - in ;
          }

          .scroll - cover {
            width: 100 % ;
            height: 100 % ;
          }

        @media(max - width: 1568 px) {
          .header - text - block {
            width: 950 px;
          }
        }

        @media(max - width: 1468 px) {
          .header - text - block {
            width: 875 px;
          }
        }

        @media(max - width: 1368 px) {
          .header - text - block {
            width: 820 px;
          }
        }

        @media(max - width: 1268 px) {
          .header - text - block {
            width: 750 px;
          }
        }

        @media(max - width: 1168 px) {
          .header - text - block {
            width: 700 px;
          }
        }

        @media(max - width: 1068 px) {
          .header - text - block {
            width: 720 px;
          }
        }

        @media(max - width: 968 px) {
          .header - text - block {
            width: 640 px;
          }
        }

        /* set container screen to 720px when screen is on portrait */
        @media screen and(max - width: 896 px) and(orientation: landscape) {
          .header - text - block {
              min - height: calc(720 px - 18 px);
            }
            .header - title - block {
              margin - top: calc(720 px - 350 px);
            }
            .scroll - btn - container {
              top: calc(720 px - 220 px);
            }
        }


        @media(max - width: 868 px) {
          .header - text - block {
            width: 560 px;
          }
        }

        @media(max - width: 768 px) {
          .scroll - cover svg {
            display: none;

          }
        }

        @media(max - width: 668 px) {
          .header - text - block {
            width: 530 px;
          }
        }

        @media(max - width: 568 px) {
          .header - text - block {
            width: 430 px;
          }
        }

        @media(max - width: 468 px) {
          .header - text - block {
              width: 96 % ;
            }
            .header - title - block {
              margin - top: calc(100 vh - 270 px);
            }
        }

        @media(max - width: 368 px) {
          .header - text - block {
              width: 98 % ;
            }
            .header - title - block {
              margin - top: calc(100 vh - 240 px);
            }
        }` 
      }</style>
    </header>
  )
}

// const mapStateToProps = (state) => {
//   return {
//     colorModes: state.state.colorModes,
//     headerPageData: state.state.pageData
//   }
// }

// export default connect(mapStateToProps)(Header)
export default Header
