import React, { createContext, useState } from 'react'

// export InputContext 
export const InputData = createContext()

// the props is received and has a value that references
// the page data sets and returns data for that page
export function InputDataContext (props) {
  const [inputValue, setInputValue] = useState(['', '', '', ''])
  const [alert, setAlert] = useState({
    message: '* required',
    color: 'red'
  })

  return (
    <InputData.Provider value={{alert}}>
      {props.children}
    </InputData.Provider>
  )
}
