import React from 'react'
import {inputvalues} from '../../utils/data/inputdata'

function Inputs() {
  
  const changeinput = (val,i) => {
    let v = values
    v[i] = val
    setState({inputvalue: v})
  }
  
  let inputs = inputvalues.map((input, i) => {
    return input.el === 'input' ? (
      <div className="input-container" key={i}>
        <i className="icon"
        style={{background: 
          `url('/images/${input.background}') no-repeat center center`}}></i>
        <input 
          className="input-field" 
          type={input.type} 
          placeholder={input.placeholder}
          name={input.name}
          value=''
          onChange={(e)=>{changeinput(e.target.value, i)}}/>
      </div>
    ) : (
      <div className="input-container" key={i}>
        <textarea 
          className="input-field message-area focus" 
          spellCheck={false}
          placeholder={input.placeholder} 
          name={input.name}
          autoFocus
          value=''
          onChange={(e)=>{changeinput(e.target.value, i)}}>
        </textarea>
      </div>
    )
  })

  return (
    <div>
      {inputs}
    </div>
  )
}

export default Inputs
