import React, { createContext, useState } from 'react'

// export InputContext 
export const InputData = createContext()

// the props is received and has a value that references
// the page data sets and returns data for that page
export function InputDataContext (props) {

  const [inputValue, setInputValue] = useState(['', '', '', ''])
  const [alert, setAlert] = useState({
    message: '* required',
    color: 'go'
  })

  function setValues (val, i) {
    console.log(val)
    let inputs = inputValue
    inputs[i] = val
    setInputValue(inputs)
  }


  return (
    <InputData.Provider value={{
        alert, 
        inputValue,
        setValues: setValues
        }}>
      {props.children}
    </InputData.Provider>
  )
}
